pragma solidity >=0.8.2 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract GitHubStaking {
    struct Stake {
        address staker;
        uint amt;
        uint pullReqId;
    }
    struct Issue {
        address creator;
        uint prize;
        bool solved;
        address solver;
        uint stakeCount;
        uint totalStakeAmt;
        mapping(uint => Stake) stakes; // index -> konsa address, kitna stakeAmt
    }

    struct Repo {
        mapping(uint => Issue) issues;
        uint issueCount;
    }
    struct WalletStats {
        uint lostStakeCount;
        uint wonStakeCount;
        uint totalAmtStaked;
        uint openAmtStaked;
        uint totalStakes;
        uint openStakes;
        uint rewardsEarned;
        uint withdraw_proceedings;
        uint lost_refund;
    }

    struct IssueStats {
        uint totalPriceAmt_SetByMe;
        uint openPriceAmt_SetByMe;
        uint totalIssues_SetByMe;
        uint openIssues_SetByMe;
    }

    struct Wallet {
        string gitUsername;
        WalletStats stats;
        IssueStats issueStats;
    }

    mapping(uint => Repo) public repos; // RepoId -> Repo
    mapping(address => Wallet) public wallets;

    uint256 private constant MIN_PRIZE = 5;
    uint private constant DEDUCTION_FACTOR = 10;
    uint256 private constant MIN_STAKE = 10;
    uint private soldTokens = 0;

    IERC20 public s_token;

    // events
    event IssueCreated(
        uint256 indexed repoId,
        uint256 indexed issueId,
        address indexed creator,
        uint256 prize
    );

    event IssueSolved(
        uint256 indexed repoId,
        uint256 indexed issueId,
        uint256 pullReqId,
        address creator,
        address indexed solver,
        uint256 prize
    );

    event StakePlaced(
        uint256 indexed repoId,
        uint256 indexed issueId,
        uint256 pullReqId,
        address indexed staker,
        uint256 amt
    );

    constructor(address token) {
        s_token = IERC20(token);
    }

    function addUserToWallet(address wallet, string memory username) public {
        wallets[wallet].gitUsername = username;
    }

    // Create a new issue with a prize
    function createIssue(
        uint repoId,
        uint issueId,
        uint256 prize
    ) public payable {
        // require(
        //     wallets[msg.sender].gitUsername != "",
        //     "Wallet not found in contract."
        // );

        Repo storage repo = repos[repoId];
        Issue storage issue = repo.issues[issueId];
        require(issue.creator == address(0), "Issue already exists");
        require(prize >= MIN_PRIZE, "Prize amount must be greater than 5");

        bool success = s_token.transferFrom(msg.sender, address(this), prize);
        if (success) {
            issue.creator = msg.sender;
            issue.prize = prize;
            issue.solved = false;
            issue.solver = address(0);
            issue.stakeCount = 0;
            issue.totalStakeAmt = 0;
            repo.issueCount++;

            wallets[msg.sender].issueStats.totalIssues_SetByMe++;
            wallets[msg.sender].issueStats.openIssues_SetByMe++;

            wallets[msg.sender].issueStats.totalPriceAmt_SetByMe =
                wallets[msg.sender].issueStats.totalPriceAmt_SetByMe +
                prize;
            wallets[msg.sender].issueStats.openPriceAmt_SetByMe =
                wallets[msg.sender].issueStats.openPriceAmt_SetByMe +
                prize;

            emit IssueCreated(repoId, issueId, msg.sender, prize);
        }
    }

    // Stake an amount on an issue
    function stakeOnIssue(
        uint repoId,
        uint issueId,
        uint pullReqId,
        uint256 amt
    ) public payable {
        require(amt >= MIN_STAKE, "Min Stake Amount is 0.01 GST.");
        Issue storage issue = repos[repoId].issues[issueId];
        require(issue.creator != msg.sender, "Issue creator cannot stake.");
        require(issue.creator != address(0), "Issue does not exist"); //address(0) represents the zero address in Solidity.
        require(issue.creator != msg.sender, "Issue creator cannot stake");
        require(!issue.solved, "Issue already solved");
        bool success = s_token.transferFrom(msg.sender, address(this), amt);
        if (success) {
            issue.stakeCount++;
            issue.stakes[issue.stakeCount].pullReqId = pullReqId;
            issue.stakes[issue.stakeCount].staker = msg.sender;
            issue.stakes[issue.stakeCount].amt = amt;
            issue.totalStakeAmt += amt;

            wallets[msg.sender].stats.totalAmtStaked += amt;
            wallets[msg.sender].stats.openAmtStaked += amt;

            wallets[msg.sender].stats.totalStakes++;
            wallets[msg.sender].stats.openStakes++;

            emit StakePlaced(repoId, issueId, pullReqId, msg.sender, amt);
        }
    }

    //  Mark an issue as solved and distribute rewards
    function markSolved(
        uint repoId,
        uint issueId,
        uint pullReqId,
        address solver
    ) public {
        require(msg.sender != solver, "Cannot declare yourself winner.");
        Issue storage issue = repos[repoId].issues[issueId];
        require(!issue.solved, "Issue already solved");
        require(
            msg.sender == issue.creator,
            "Only issue creator can mark as solved"
        );
        updateWalletForSolvedIssue(solver, issue.prize);

        issue.solved = true;
        issue.solver = solver;

        rejectOthers(repoId, issueId, pullReqId, solver);

        emit IssueSolved(
            repoId,
            issueId,
            pullReqId,
            msg.sender,
            solver,
            issue.prize
        );
    }

    function updateWalletForSolvedIssue(address solver, uint prize) internal {
        wallets[solver].stats.wonStakeCount++;
        wallets[solver].stats.rewardsEarned += prize;
        wallets[solver].stats.withdraw_proceedings += prize;
    }

    function rejectOthers(
        uint repoId,
        uint issueId,
        uint pullReqId,
        address solver
    ) public {
        Issue storage issue = repos[repoId].issues[issueId];
        // Deduct from other stakers and refund the remaining amount
        for (uint i = 0; i < issue.stakeCount; i++) {
            address staker = issue.stakes[i].staker;
            wallets[staker].stats.openAmtStaked =
                wallets[staker].stats.openAmtStaked -
                issue.stakes[i].amt;
            if (staker == solver && issue.stakes[i].pullReqId == pullReqId) {
                wallets[solver].stats.withdraw_proceedings += issue
                    .stakes[i]
                    .amt;
                continue;
            }

            uint deduction = ((issue.stakes[i].amt / issue.stakeCount) *
                DEDUCTION_FACTOR);
            uint refund = issue.stakes[i].amt - deduction;
            wallets[staker].stats.lostStakeCount++;
            wallets[staker].stats.lost_refund += refund;
            wallets[staker].stats.withdraw_proceedings -= refund;
        }
    }

    // Withdraw refund balance
    function withdraw_reward() public payable {
        uint amount = wallets[msg.sender].stats.withdraw_proceedings;
        require(amount > 0, "Nothing to withdraw.");
        wallets[msg.sender].stats.withdraw_proceedings = 0;
        bool success = s_token.transfer(msg.sender, amount);
        require(success, "Transfer failed.");
        soldTokens = soldTokens + amount;
    }

    function getBasicWalletDetails()
        public
        view
        returns (
            string memory gitUsername,
            uint lostStakeCount,
            uint wonStakeCount
        )
    {
        Wallet storage wallet = wallets[msg.sender];
        return (
            wallet.gitUsername,
            wallet.stats.lostStakeCount,
            wallet.stats.wonStakeCount
        );
    }

    function getStakingWalletDetails()
        public
        view
        returns (
            uint totalAmtStaked,
            uint openAmtStaked,
            uint totalStakes,
            uint openStakes
        )
    {
        Wallet storage wallet = wallets[msg.sender];
        return (
            wallet.stats.totalAmtStaked,
            wallet.stats.openAmtStaked,
            wallet.stats.totalStakes,
            wallet.stats.openStakes
        );
    }

    function getRewardsWalletDetails()
        public
        view
        returns (
            uint rewardsEarned,
            uint withdraw_proceedings,
            uint lost_refund
        )
    {
        Wallet storage wallet = wallets[msg.sender];
        return (
            wallet.stats.rewardsEarned,
            wallet.stats.withdraw_proceedings,
            wallet.stats.lost_refund
        );
    }

    function getIssueStats()
        public
        view
        returns (
            uint totalPriceAmt_SetByMe,
            uint openPriceAmt_SetByMe,
            uint totalIssues_SetByMe,
            uint openIssues_SetByMe
        )
    {
        Wallet storage wallet = wallets[msg.sender];
        return (
            wallet.issueStats.totalPriceAmt_SetByMe,
            wallet.issueStats.openPriceAmt_SetByMe,
            wallet.issueStats.totalIssues_SetByMe,
            wallet.issueStats.openIssues_SetByMe
        );
    }

    function getTotalStakeAmount(
        uint repoId,
        uint issueId
    ) public view returns (uint256) {
        Issue storage issue = repos[repoId].issues[issueId];
        return issue.totalStakeAmt;
    }

    function getTotalStakers(
        uint repoId,
        uint issueId
    ) public view returns (uint256) {
        Issue storage issue = repos[repoId].issues[issueId];
        return issue.stakeCount;
    }

    function getIssue(
        uint repoId,
        uint issueId
    )
        public
        view
        returns (
            address creator,
            uint prize,
            bool solved,
            address solver,
            uint stakeCount,
            uint totalStakeAmt
        )
    {
        Issue storage issue = repos[repoId].issues[issueId];
        require(issue.creator != address(0), "Issue does not exist");
        return (
            issue.creator,
            issue.prize,
            issue.solved,
            issue.solver,
            issue.stakeCount,
            issue.totalStakeAmt
        );
    }

    function getStakeCount(
        uint repoId,
        uint issueId
    ) public view returns (uint) {
        return repos[repoId].issues[issueId].stakeCount;
    }

    function getStake(
        uint repoId,
        uint issueId,
        uint index
    ) public view returns (address staker, uint amt) {
        Issue storage issue = repos[repoId].issues[issueId];
        Stake storage stake = issue.stakes[index];
        require(issue.creator != address(0), "Issue does not exist");
        return (stake.staker, stake.amt);
    }

    function getEstDeductionRateOnIssue(
        uint repoId,
        uint issueId,
        uint approx_amt
    ) public view returns (uint) {
        Issue storage issue = repos[repoId].issues[issueId];
        uint deduction = ((approx_amt / issue.stakeCount) * DEDUCTION_FACTOR);
        return deduction;
    }

    function getDeductionFactor() public pure returns (uint) {
        return DEDUCTION_FACTOR;
    }

    function getSoldTokens() public view returns(uint) {
        return soldTokens;
    }
}
